import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import "primereact/resources/themes/nova-light/theme.css";
import "primereact/resources/primereact.min.css";
import "primereact/resources/primeflex.css";
import "primeicons/primeicons.css";
import "../admin.css";

import { InputText } from "primereact/inputtext";
import { Password } from "primereact/password";
import { Checkbox } from "primereact/checkbox";
import { Button } from "primereact/button";
import { NavLink } from "react-router-dom";

import { PostData } from "../../../services/PostData";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      remember_me: "",
      isLoggedIn: false
    };
    this.login = this.login.bind(this);
    this.onChange = this.onChange.bind(this);
  }

  login() {
    if (this.state.email && this.state.password) {
      PostData("login", this.state).then(result => {
        let reponseJSON = result;
        if (reponseJSON.data) {
          sessionStorage.setItem("user", reponseJSON);
          this.setState({ isLoggedIn: true });
        } else {
          console.log("Login error!");
        }
      });
    }
  }

  onChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
    //console.log(this.state);
  }

  render() {
    if (this.state.isLoggedIn) {
      return <Redirect to="/admin/dashboard" />;
    }

    if (sessionStorage.getItem("user")) {
      return <Redirect to="/admin/dashboard" />;
    }

    return (
      <div className="p-grid p-justify-center" style={{ height: "100vh" }}>
        <div className="p-sm-12 p-md-4 p-lg-3 p-xl-3 p-col-align-center">
          <div className="p-col-12">
            <div className="p-inputgroup">
              <span className="p-inputgroup-addon">
                <i className="pi pi-envelope" />
              </span>
              <InputText
                name="email"
                placeholder="Email"
                onChange={this.onChange}
              />
            </div>
          </div>
          <div className="p-col-12">
            <div className="p-inputgroup">
              <span className="p-inputgroup-addon">
                <i className="pi pi-lock" />
              </span>
              <Password
                id="password"
                name="password"
                value={this.state.value}
                onChange={this.onChange}
                placeholder="Password"
              />
            </div>
          </div>
          <div className="p-col p-grid">
            <div className="p-col-7">
              <Checkbox
                inputId="remember_me"
                name="remember_me"
                value="remember"
                checked={this.state.isChecked}
                onChange={this.onChange}
              />
              <label
                htmlFor="remember_me"
                className="p-checkbox-label remember-me"
              >
                Remember me
              </label>
            </div>
            <div className="p-col-5 text-right">
              <Button
                label="Login"
                className="p-button-raised p-button-secondary"
                onClick={this.login}
              />
            </div>
          </div>
          <div className="p-col-12">
            <div className="reset-password">
              <NavLink to="/admin/login/reset-password">
                <span>Forgot your password?</span>
              </NavLink>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
