import React, { Component } from "react";
import PropTypes from "prop-types";
import { withStyles, createMuiTheme } from "@material-ui/core/styles";
import CircularProgress from "@material-ui/core/CircularProgress";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";

import { spinnerService } from "../services/Spinner.services";

const styles = theme => ({
  root: {
    flexGrow: 1,
    height: "100vh",
    padding: "0 8%"
  },
  spinnerContainer: {
    height: "100vh"
  },
  progress: {
    margin: theme.spacing.unit * 2
  },
  textCenter: {
    textAlign: "center"
  }
});

const theme = createMuiTheme({
  typography: {
    useNextVariants: true
  }
});

export class SpinnerComponent extends Component {
  state = {};

  get name() {
    return this.props.name;
  }

  get group() {
    return this.props.group;
  }

  get show() {
    return this.state.show;
  }

  set show(show) {
    this.setState({ show });
  }

  constructor(props, context) {
    super(props, context);

    this.state = {
      show: this.props.hasOwnProperty("show") ? this.props.show : false,
      classes: []
    };

    if (this.props.hasOwnProperty("spinnerService")) {
      this.spinnerService = this.props.spinnerService;
    } else {
      this.spinnerService = spinnerService;
    }

    this.spinnerService._register(this);
  }

  componentWillUnmount() {
    this.spinnerService._unregister(this);
  }

  render() {
    const { classes } = this.props;
    if (this.state.show) {
      const { loading } = this.props;
      return (
        <div className={classes.root}>
          <Grid
            className={classes.spinnerContainer}
            container
            direction="row"
            justify="center"
            alignItems="center"
          >
            <Grid item xs={12} className={classes.textCenter}>
              <CircularProgress
                className={classes.progress}
                variant="determinate"
                value={this.state.completed}
              />
              <Typography component="p">{this.props.children}</Typography>
            </Grid>
          </Grid>
        </div>
      );
    }

    return (
      <div className={classes.root}>
        <Grid
          className={classes.spinnerContainer}
          container
          direction="row"
          justify="center"
          alignItems="center"
        >
          <Grid item xs={12} className={classes.textCenter}>
            <CircularProgress
              className={classes.progress}
              variant="determinate"
              value={this.state.completed}
            />
            <Typography component="p">Please wait loading...</Typography>
          </Grid>
        </Grid>
      </div>
    );
  }
}

SpinnerComponent.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles, { withTheme: true })(SpinnerComponent);
