import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import Header from "./elements/header";
import PropTypes from "prop-types";
import Typography from "@material-ui/core/Typography";
import { withStyles, createMuiTheme } from "@material-ui/core/styles";

import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import Chip from "@material-ui/core/Chip";
import Button from "@material-ui/core/Button";
import Avatar from "@material-ui/core/Avatar";

import Dboard from "@material-ui/icons/Dashboard";
import SupervisorAccount from "@material-ui/icons/SupervisorAccountOutlined";

import Loader from "../Loader";

const styles = theme => ({
  root: {
    flexGrow: 1
  },
  content: {
    flexGrow: 1,
    padding: "6rem 1.5rem"
  },
  paper: {
    padding: theme.spacing.unit * 4,
    color: theme.palette.text.secondary
  },
  toolbar: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    padding: "0 8px",
    ...theme.mixins.toolbar
  },
  breadCrumbs: {
    marginBottom: "1rem"
  },
  update: {
    padding: "0rem 2rem"
  },
  chip: {
    marginRight: theme.spacing.unit
  },
  textCenter: {
    textAlign: "center"
  },
  iconSize: {
    fontSize: "6rem"
  }
});

const theme = createMuiTheme({
  typography: {
    useNextVariants: true
  }
});

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: false,
      loading: true
    };
  }

  componentDidMount() {
    if (sessionStorage.getItem("user")) {
    } else {
      this.setState({ isLoggedIn: true });
      //this.setState({ loading: false });
    }
    this.setState({ loading: false });
  }

  render() {
    const { classes } = this.props;
    const { loading } = this.state;
    // if (this.state.isLoggedIn) {
    //   return <Redirect to="/admin/login" />;
    // }

    return (
      <div>
        {loading ? (
          <Loader />
        ) : (
          <main className={classes.content}>
            <Grid className={classes.loginContainer} container direction="row">
              <Grid
                item
                className={classes.breadCrumbs}
                sm={12}
                md={9}
                lg={9}
                xs={12}
                xl={9}
              >
                <Chip
                  avatar={
                    <Avatar>
                      <Dboard />
                    </Avatar>
                  }
                  label="Dashboard"
                  className={classes.chip}
                />
              </Grid>
              <Grid item sm={12} md={9} lg={9} xs={12} xl={9}>
                <Grid
                  className={classes.loginContainer}
                  container
                  direction="row"
                >
                  <Grid
                    item
                    className={classes.textCenter}
                    sm={12}
                    md={4}
                    lg={3}
                    xs={12}
                    xl={3}
                  >
                    <Link to="/admin/users">
                      <SupervisorAccount className={classes.iconSize} />
                      <Typography variant="h6" id="tableTitle">
                        User Accounts
                      </Typography>
                    </Link>
                  </Grid>
                </Grid>
              </Grid>
              <Grid
                className={classes.update}
                item
                sm={12}
                md={3}
                lg={3}
                xs={12}
                xl={3}
              >
                {/* <Typography variant="h6" component="h5">
                  Updates
                </Typography> */}
              </Grid>
            </Grid>
          </main>
        )}
      </div>
    );
  }
}

Dashboard.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles, { withTheme: true })(Dashboard);
